    this.playbackSmoothing = 0;
    this.playbackSkipDigitalSilence = false;
    this.playbackMinBuffers = 24; // 24 * 128 samples @ 24kHz = 128ms
    
    // state
    this.playbackOutputOffset = 0;
    this.isInPlayback = false;
    
    this.trackSampleOffsets = {};
